mapper.select:
select distinct D.pk_daily_ta_working_date,c.userno,s.sawon_name, s.fk_dept_name, s.rank_name, D.daily_ta_go_time, D.daily_ta_off_time, D.daily_ta_work_time, D.daily_ta_holi_work, D.daily_ta_exten_work, D.daily_ta_night_work, D.daily_ta_late_time, D.daily_ta_leave_time from com_loginout c, sawon s ,daily_ta d
c.userno = s.pk_sawon_code and c.userno = #{PK_SAWON_CODE, jdbcType=VARCHAR}
and d.pk_daily_ta_working_date = #{PK_DAILY_TA_WORKING_DATE}

select D.PK_DAILY_TA_WORKING_DATE,S.SAWON_NAME, D.DAILY_TA_SAWON_NAME, D.DAILY_TA_RANK, D.DAILY_TA_DEPT_NAME, D.DAILY_TA_TA, D.DAILY_TA_GO_TIME, D.DAILY_TA_OFF_TIME, D.DAILY_TA_WORK_TIME,D.DAILY_TA_HOLI_WORK,D.DAILY_TA_EXTEN_WORK, D.DAILY_TA_NIGHT_WORK,D.DAILY_TA_LATE_TIME,D.DAILY_TA_LEAVE_TIME from DAILY_TA D, SAWON S
D.FK_DAILY_TA_SAWON_CODE = S.PK_SAWON_CODE and PK_DAILY_TA_WORKING_DATE between #{PK_DAILY_TA_WORKING_DATE2} and #{PK_DAILY_TA_WORKING_DATE3}
sawon_name = #{sawon_name}
